mapper.insert:
insert into zl_answer (id, uid, qid, 
  create_time, examine, content, support_nums
  )
values (#{id,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER}, #{qid,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{examine,jdbcType=INTEGER}, #{content,jdbcType=LONGVARCHAR}, #{supportNums,jdbcType=INTEGER}
  )

mapper.update:
update zl_answer
set uid = #{uid,jdbcType=INTEGER},
  qid = #{qid,jdbcType=INTEGER},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  examine = #{examine,jdbcType=INTEGER}
  support_nums = #{supportNums,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT temp.uid,temp.lev,temp.recommendReason,cu.nickname,cu.headerimg,cu.intro,cu.role FROM (SELECT a.uid,1 as lev,'指定回答者' as recommendReason FROM zl_answer_recommend as a WHERE a.type = #{type} and a.info_id = #{infoId}
UNION SELECT cae.uid,2 as lev,'该案例编辑用户' as recommendReason FROM zl_case_edit as cae WHERE cae.aid = #{infoId} AND cae.examine = 1
UNION SELECT b.uid,3 as lev,'问题回答用户' as recommendReason FROM zl_answer as b LEFT JOIN zl_questions as c ON c.id = b.qid WHERE c.info_class = #{type} and c.iid = #{infoId} and b.examine = 1
UNION SELECT cu.id uid,4 AS lev,'生产商旗下工程师' AS recommendReason FROM zl_application_case a LEFT JOIN zl_specification s ON a.sid=s.id LEFT JOIN zl_customer c ON c.id = s.mid LEFT JOIN zl_company com ON com.parent_id = c.id LEFT JOIN zl_customer cu ON cu.company_id = com.id WHERE c.role=2 AND c.del = 1 AND com.status=0 AND a.id=#{infoId} GROUP BY cu.id
UNION SELECT cu.id uid,4 AS lev,'生产商旗下工程师' AS recommendReason FROM zl_specification s LEFT JOIN zl_customer c ON c.id = s.mid LEFT JOIN zl_company com ON com.parent_id = c.id LEFT JOIN zl_customer cu ON cu.company_id = com.id WHERE c.role=2 AND c.del = 1 AND com.status=0 AND s.id=#{infoId} GROUP BY cu.id
) as temp LEFT JOIN zl_customer as cu on cu.id = temp.uid WHERE cu.role in (1,6) and cu.del = 1 GROUP BY temp.uid ORDER BY temp.lev ASC
