mapper.select:
select b.id, b.title, m.`name` as terminal,n.`name` as appProduct, c.name as specName, b.photo, b.album, b.create_time as createTime, a.exhibition_case_id as exhibitionCaseId,a.exhibition_id as exhibitionId from zl_exhibition_case_map as a left join zl_application_case as b on b.id = a.case_id left join zl_specification as c on c.id = b.sid left join zl_specification_parameter as m on m.id = b.terminal_id left join zl_specification_parameter as n on n.id = b.app_product_id where a.exhibition_id = #{exhibitionId}
and n.`name` LIKE CONCAT('%',#{caseName},'%') and n.class_type = 9
and c.mid = #{manufacturerId}
order by b.create_time desc

SELECT a.title,s.name specificationName,zsp01.name appProductName,a.create_time createTime,IF(ecm.id>0,1,0) relevance,IF(ec.id>0,1,0) relevanceCustomere,a.id aid,ecm.exhibition_id eid,c.nickname FROM zl_application_case a LEFT JOIN zl_specification s ON a.sid=s.id LEFT JOIN zl_specification_parameter zsp01 ON zsp01.id=a.app_product_id AND zsp01.class_type=9 LEFT JOIN zl_exhibition_case_map ecm ON ecm.case_id=a.id AND ecm.exhibition_id=#{id} LEFT JOIN zl_exhibition_case ec ON ec.id = ecm.exhibition_case_id AND ec.del = 1 LEFT JOIN zl_customer c ON ec.uid = c.id and c.del = 1
LEFT JOIN zl_customer cs ON s.mid = cs.id and cs.del = 1
WHERE a.del = 1
and a.title LIKE CONCAT('%',#{key},'%')
and cs.id = #{mid}
and a.industry_id = #{industryId}
and a.market_id = #{marketId}
ORDER BY relevance desc,a.id DESC
