mapper.select:
select a.id,a.uid,b.nickname,b.headerimg,b.intro,a.iid,a.info_class as infoClass,a.module_type as moduleType, a.title,a.content,a.create_time as createTime,a.examine,c.id as collectId, CASE WHEN c.id IS NULL then 0 else 1 end as isCollect, CASE WHEN ca.title IS NULL then spec.`name` else ca.title end as source, (SELECT COUNT(*) FROM zl_answer as aw WHERE aw.qid = a.id and aw.examine = 1 ) as answerCount from zl_questions as a left join zl_customer as b on b.id = a.uid left join zl_collection as c on c.iid = a.id and c.type = 3 and c.uid = #{userId} left join zl_specification as spec on spec.id = a.iid and a.info_class = 1 left join zl_application_case as ca on ca.id = a.iid and a.info_class = 2 where a.uid = #{userId}
and a.info_class = #{infoClass}
order by a.create_time desc

	select COUNT(*) 
 from zl_questions 
 where uid = #{userId}

select a.id,a.uid,b.nickname,b.headerimg,b.intro,a.iid,a.info_class as infoClass,a.module_type as moduleType, a.title,a.content,a.create_time as createTime,a.examine,c.id as collectId, 1 as isCollect, CASE WHEN ca.title IS NULL then spec.`name` else ca.title end as source, (SELECT COUNT(*) FROM zl_answer as aw WHERE aw.qid = a.id and aw.examine = 1 ) as answerCount from zl_collection as c left join zl_questions as a on c.iid = a.id left join zl_customer as b on b.id = a.uid left join zl_specification as spec on spec.id = a.iid and a.info_class = 1 left join zl_application_case as ca on ca.id = a.iid and a.info_class = 2 where a.examine = 1 and c.uid = #{userId} and c.type = 3
and a.info_class = #{infoClass}
order by a.create_time desc

select DISTINCT a.id,a.uid,b.nickname,b.headerimg,b.intro,a.iid,a.info_class as infoClass,a.module_type as moduleType, a.title,a.content,a.create_time as createTime,a.examine,c.id as collectId, CASE WHEN c.id IS NULL then 0 else 1 end as isCollect, CASE WHEN ca.title IS NULL then spec.`name` else ca.title end as source, (SELECT COUNT(*) FROM zl_answer as aw WHERE aw.qid = a.id and aw.examine = 1 ) as answerCount from zl_questions as a left join zl_customer as b on b.id = a.uid left join zl_collection as c on c.iid = a.id and c.type = 3 and c.uid = #{userId} left join zl_specification as spec on spec.id = a.iid and a.info_class = 1 left join zl_application_case as ca on ca.id = a.iid and a.info_class = 2 left join zl_answer as ans on ans.qid = a.id where ans.uid = #{userId} and a.examine = 1 and ans.examine = 1
and a.info_class = #{infoClass}
order by a.create_time desc

